var mySwiper=new Swiper(".swiper-container",{speed:400,slidesPerView:3,loop:!0,navigation:{nextEl:".swiper-next",prevEl:".swiper-prev"}});const anchors=[].slice.call(document.querySelectorAll('a[href*="#"]')),animationTime=300,framesCount=20;anchors.forEach(function(e){e.addEventListener("click",function(n){n.preventDefault();let t=document.querySelector(e.getAttribute("href")).getBoundingClientRect().top+window.pageYOffset,i=setInterval(function(){let e=t/20;e>window.pageYOffset-t&&window.innerHeight+window.pageYOffset<document.body.offsetHeight?window.scrollBy(0,e):(window.scrollTo(0,t),clearInterval(i))},15)})});